function rewardTrade(player,npc,trade)

    local totalBayld = player:getCurrency("bayld");                                         --Get players total Bayld
    local totalPlasm = player:getCurrency("mweya_plasm");                                   --Get players total Mweya Plasm
    local totalNotes = player:getCurrency("dominion_note");                                 --Get players total Dominion Notes
    local totalUnits = player:getCurrency("kinetic_unit");                                  --Get players total Kinetic Units


    local tradedItem = trade:getItem();                                                     --Get the Item ID of the Item Traded
    local qty        = trade:getItemQty(tradedItem);                                        --Get the Quantity of the Item Traded
    local found      = rewardItem[tradedItem];                                              --Set a variable to see if it's in the table
 
if found then                                                                               --See if the item is found
    if found[qty] then                                                                      --See if the items quantity is found

        local rewards = found[qty];                                                         --Set our table to the corrisponding rewards table.
        for _, item in ipairs(rewards.items) do

            local rItem = item[1];                                                          --Set the reward item to a variable for easier handling.
            local rQty  = item[2];                                                          --Set the reward quantity to a variable for easier handling.
            local cost  = item[3];                                                          --Set the currency cost to a variable for easier handling.

            if (player:getFreeSlotsCount() > 0) and (player:hasItem(rItem) == true) then    --Check to see if player has the item already and inventory space.
                player:PrintToPlayer("You already own that item!");                         --If they do, let them know they do.
            elseif (player:getFreeSlotsCount() == 0) then                                   --Check inventory for space.
                player:messageSpecial(ITEM_CANNOT_BE_OBTAINED,rItem,1);                     --If no space, message them to sort inventory.  
            elseif (tradedItem == 8798) and (player:getFreeSlotsCount() > 0) then           --Begin check for bayld deletion and adding item.
                    if (cost <= totalBayld) then                                            --If you have enough bayld, begin adding, messaging, and deleting.
                        player:addItem(rItem,rQty);
                        player:messageSpecial(ITEM_OBTAINED,rItem,1);
                        player:tradeComplete();                                             --Deletes items that were traded.
                        player:delCurrency("bayld",cost);
                    elseif (cost >= totalBayld) then                                        --If not enough bayld.
                        player:PrintToPlayer("You have not enough Bayld!");
                    end
            elseif (tradedItem == 4059) and (player:getFreeSlotsCount() > 0) then           --Begin check for plasm deletion and adding item.
                    if (cost <= totalPlasm) then                                            --If you have enough plasm, begin adding, messaging, and deleting.
                        player:addItem(rItem,rQty);
                        player:messageSpecial(ITEM_OBTAINED,rItem,1);
                        player:tradeComplete();                                             --Deletes items that were traded.
                        player:delCurrency("mweya_plasm",cost);
                    elseif (cost >= totalPlasm) then                                        --If not enough plasm.
                        player:PrintToPlayer("You have not enough Plasm!")
                    end
            elseif (tradedItem == 4060) and (player:getFreeSlotsCount() > 0) then           --Begin check for notes deletion and adding item.
                    if (cost <= totalNotes) then                                            --If you have enough notes, begin adding, messaging, and deleting.
                        player:addItem(rItem,rQty);                                         
                        player:messageSpecial(ITEM_OBTAINED,rItem,1);
                        player:tradeComplete();                                             --Deletes items that were traded.
                        player:delCurrency("dominion_note",cost);
                    elseif (cost >= totalNotes) then                                        --If not enough notes.
                        player:PrintToPlayer("You have not enough Notes!")
                    end
            elseif (tradedItem == 4061) and (player:getFreeSlotsCount() > 0) then           --Begin check for units deletion and adding item.
                    if (cost <= totalUnits) then                                            --If you have enough units, begin adding, messaging, and deleting.
                        player:addItem(rItem,rQty);
                        player:messageSpecial(ITEM_OBTAINED,rItem,1);
                        player:tradeComplete();                                             --Deletes items that were traded.
                        player:delCurrency("kinetic_unit",cost);
                    elseif (cost >= totalUnits) then                                        --If not enough units.
                        player:PrintToPlayer("You have not enough Units!")
                    end
            end
        end
    elseif not found[qty] then                                                              --Check to make sure they traded a valid number of items.            
        player:PrintToPlayer("Invalid Number of Items Traded: "..qty);  
    end
elseif not found then                                                                       --Check to make sure they traded a correct item.
    player:PrintToPlayer("Invalid Item Traded: "..tradedItem);
end
end;

